import React, { FC, useEffect, useState } from 'react'
import Container from '../../../../components/container/Container';
import Readmore from './components/Readmore/Readmore';
import './Slider.scss'

interface SliderProps {
	header: number;
}

const Slider: FC<SliderProps> = ({ header }) => {

	// let style = { 'height': window?.innerHeight - header } as React.CSSProperties;
	const [loaded, setLoaded] = useState<boolean>(false)


	const handleScroll = () => {
		setLoaded(true)
		console.log('da')
	};
	useEffect(() => {
		window.addEventListener("DOMContentLoaded", handleScroll);
		return () => window.removeEventListener("DOMContentLoaded", handleScroll);
	}, []);

	return (
		<section className='slider'>
			<Readmore />
			<Container>
				<div className='slider__buttons'>
					<button className='slider__button available'></button>
					<button className='slider__button'></button>
				</div>
			</Container>
		</section>
	)
}

export default Slider
