import React, { FC, useEffect, useState } from 'react'
import Container from '../../../../components/container/Container';
import Readmore from './components/Readmore/Readmore';
import './Slider.scss'

interface SliderProps {
	header: number;
}

const Slider: FC<SliderProps> = ({ header }) => {

	// let style = { 'height': window?.innerHeight - header } as React.CSSProperties;
	const [slide, setSlide] = useState<number>(1)
	const [loaded, setLoaded] = useState<boolean>(false)


	const handleScroll = () => {
		setLoaded(true)
	};
	useEffect(() => {
		window.addEventListener("load", handleScroll);
		return () => window.removeEventListener("load", handleScroll);
	}, []);

	return (
		<section className='slider'>
			<Readmore loaded={loaded} />
			<Container>
				<div className='slider__buttons'>
					<button className={['slider__button', slide === 1 ? 'available' : ''].join(' ')} onClick={() => {
						setSlide(1)
					}}></button>
					<button className={['slider__button', slide === 2 ? 'available' : ''].join(' ')} onClick={() => {
						setSlide(2)
					}}></button>
				</div>
			</Container>
		</section>
	)
}

export default Slider
