import React, { FC, ReactElement, ReactNode, useEffect, useRef, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import Container from '../container/Container'
import Top from './components/top/Top'
import './Header.scss'

interface HeaderProps {

}

const Header: FC<HeaderProps> = ({ }) => {
	const [availableBurger, setAvailableBurger] = useState(false)
	const [visibleBurger, setVisibleBurger] = useState(false)
	const [scroll, setScroll] = useState<number>(0)
	const header = useRef<null | any>(null)
	const fixedHeader = useRef<null | any>(null)

	const handleScroll = () => {
		setScroll(window.scrollY);
	};
	useEffect(() => {
		window.addEventListener("scroll", handleScroll);
		return () => window.removeEventListener("scroll", handleScroll);
	}, []);


	return (
		<header className='header' ref={header}>
			<Container>

				<Top />
				<Burger
					availableBurger={availableBurger}
					visibleBurger={visibleBurger}
					setAvailableBurger={setAvailableBurger}
					setVisibleBurger={setVisibleBurger}
				/>
				<section className={['header__bottom', scroll > header.current?.offsetHeight - fixedHeader.current?.offsetHeight ? 'available' : ''].join(' ')} ref={fixedHeader}>
					<div className='header__burger-button' onClick={() => { setAvailableBurger(true); setTimeout(() => setVisibleBurger(true), 10) }}>
						<span></span>
						<span></span>
						<span></span>
					</div>
					<nav className='header__navigation navigation'>
						<button className='navigation__element'>
							<span className='navigation__name'>Home</span>
							<div className='navigation__icon'>
							</div>
						</button>
						<button className='navigation__element'>
							<span className='navigation__name'>About</span>
							<div className='navigation__icon'>
							</div>
						</button>
						<button className='navigation__element'>
							<span className='navigation__name'>Services</span>
							<div className='navigation__icon'>
							</div>
						</button>
						<button className='navigation__element'>
							<span className='navigation__name'>Contact</span>
							<div className='navigation__icon'>
							</div>
						</button>
					</nav>
					{/* <section className='header__search search'>
						<input className='search__input' type='text' placeholder='ENTER KEYWORD' />
						<input className='search__button' type='button' />
					</section> */}
				</section>
			</Container>
		</header >
	)
}

export default Header
